  getHeadquarters() {
    return [
      {value: true, caption: 'Todos', counter: 100},
      {value: true, caption: 'Ceabe', counter: 75},
      {value: true, caption: 'Rebalgiati', counter: 25}
    ]
  }

  getCategories(data: any): Observable<any> {
    return this.http.post<any>(environment.url + "/categoria/listar", data)
  }


  getQuoteList(data: any): Observable<any> {
    return this.http.post<any>(environment.url + "/cotizacion/listar", data)
  }

  getQuote(id: string): Quote | undefined {
    return this.quotes.find(q => q.id === id);
  }

  sendQuote(id: string): void {
    const quote = this.getQuote(id);
    if (quote) {
      quote.postulated = true;
    }
    this.refreshQuoteList(id);
  }

  private refreshQuoteList(id: string) {
    const quote = this.quoteList.find(q => q.id === id);
    if (quote) {
      quote.postulated = true;
    }
  }
